{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "properties": {
    "related": {
      "type": "boolean",
      "description": "Indicates whether the job title is related to a managerial or leadership role in AI, ML, or Data Science"
    }
  },
  "required": ["related"],
  "additionalProperties": false
}

{
  "type": "object",
  "properties": {
    "domain_name": {
      "type": "string",
      "description": "The main domain name extracted from the text (e.g., example.com). It should exclude any protocol (http/https), paths, or query parameters."
    }
  },
  "required": ["domain_name"]
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "JobOfferEvaluation",
  "description": "Schema for evaluating whether a job offer is adapted to level data-related position and detecting its language.",
  "type": "object",
  "properties": {
    "label": {
      "type": "string",
      "enum": ["Adapted", "Not Adapted"],
      "description": "Indicates whether the job offer is adapted or not."
    },
    "explanation": {
      "type": "string",
      "description": "A concise explanation describing why the job offer was classified as adapted or not."
    },
    "language": {
      "type": "string",
      "description": "The detected language of the job offer, such as 'English', 'French', 'Arabic', etc."
    }
  },
  "required": ["label", "explanation", "language"],
  "additionalProperties": false
}

{
  "type": "object",
  "properties": {
    "subject": {
      "type": "string",
      "description": "The subject of the cover letter (e.g., 'Application for the position of Data Analyst')."
    },
    "content": {
      "type": "string",
      "description": "The full content of the cover letter in French."
    }
  },
  "required": ["subject", "content"]
}
